import React, { useState, useRef } from 'react';
import './ExpenseForm.css';
export const ExpenseForm = ({ expenses, setExpenses }) => {
  const incrementer = useRef(5);
  const [formValues, setFormValues] = useState({
    id: '',
    title: '',
    amount: '',
    date: '',
  });
  const titleChangeHandler = (event) => {
    setFormValues({ ...formValues, [event.target.name]: event.target.value });
  };

  const amountChangeHandler = (event) => {
    console.log(event.target.name);
    setFormValues({ ...formValues, [event.target.name]: event.target.value });
  };

  const dateChangeHandler = (event) => {
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value,
    });
  };

  const submitFormHandler = (event) => {
    event.preventDefault();
    let identifier = incrementer;

    setFormValues({ ...formValues, id: (identifier += 1) });
    console.log(identifier);
    const expenseCopy = [...expenses];
    console.log(formValues);
    const expenseNew = { ...formValues };
    console.log(expenseNew.id);
    expenseNew.date = new Date(formValues.date);
    expenseCopy.push(expenseNew);
    setExpenses(expenseCopy);
    setFormValues({ ...formValues, title: '', amount: '', date: '' });
    incrementer.current = identifier;
  };
  return (
    <form onSubmit={submitFormHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label htmlFor="title">Title</label>
          <input
            id="title"
            type="text"
            name="title"
            value={formValues.title}
            onChange={titleChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label htmlFor="amount">Amount</label>
          <input
            name="amount"
            id="amount"
            value={formValues.amount}
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label htmlFor="date">Amount</label>
          <input
            value={formValues.date}
            name="date"
            id="date"
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add expense</button>
      </div>
    </form>
  );
};
